import { useEffect } from 'react';
import styles from './Header.module.css';
import { Link } from 'react-router-dom';

const Header = () => {
    useEffect(() => {
        const scrollAnimElements = document.querySelectorAll('[data-animate-on-scroll]');
        const observer = new IntersectionObserver(
            (entries) => {
                for (const entry of entries) {
                    if (entry.isIntersecting || entry.intersectionRatio > 0) {
                        const targetElement = entry.target;
                        targetElement.classList.add(styles.animate);
                        observer.unobserve(targetElement);
                    }
                }
            },
            {
                threshold: 0.15,
            },
        );

        for (let i = 0; i < scrollAnimElements.length; i++) {
            observer.observe(scrollAnimElements[i]);
        }

        return () => {
            for (let i = 0; i < scrollAnimElements.length; i++) {
                observer.unobserve(scrollAnimElements[i]);
            }
        };
    }, []);

    return (
        <div className={styles.header}>
            <div className={styles.logo}>
                <div className={styles.text}>SCSS</div>
                <img className={styles.frameIcon} alt="" src="/frame.svg" />
            </div>
            <div className={styles.nav}>
                <button className={styles.home}>Home</button>
                <button className={styles.service}>SERVICE</button>
                <button className={styles.service}>About</button>
                <button className={styles.pages}>Pages</button>
                <button className={styles.blog}>Blog</button>
                <button className={styles.contact}>Contact</button>
            </div>
            <img className={styles.activeIcon} alt="" src="/active.svg" />
            <button className={styles.button} data-animate-on-scroll>
                <Link to={'/login'} className={styles.signUp}>
                    Sign Up
                </Link>
            </button>
            <img className={styles.iconexlightsearch} alt="" src="/iconexlightsearch.svg" />
            <img className={styles.iconexlightbag2} alt="" src="/iconexlightbag-2.svg" />
            <img className={styles.activeIcon1} alt="" src="/active1.svg" />
        </div>
    );
};

export default Header;
