import { useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form } from 'react-bootstrap';
import styles from './FooterForm.module.css';

const FooterForm = () => {
    useEffect(() => {
        const scrollAnimElements = document.querySelectorAll('[data-animate-on-scroll]');
        const observer = new IntersectionObserver(
            (entries) => {
                for (const entry of entries) {
                    if (entry.isIntersecting || entry.intersectionRatio > 0) {
                        const targetElement = entry.target;
                        targetElement.classList.add(styles.animate);
                        observer.unobserve(targetElement);
                    }
                }
            },
            {
                threshold: 0.15,
            },
        );

        for (let i = 0; i < scrollAnimElements.length; i++) {
            observer.observe(scrollAnimElements[i]);
        }

        return () => {
            for (let i = 0; i < scrollAnimElements.length; i++) {
                observer.unobserve(scrollAnimElements[i]);
            }
        };
    }, []);

    return (
        <div className={styles.footer}>
            <div className={styles.background} />
            <div className={styles.copyright20222023}>Â© Copyright 2022-2023 - Capton2SE13</div>
            <div className={styles.div}>
                <div className={styles.newsletter}>Newsletter</div>
                <div className={styles.subscribeToOur}>
                    Subscribe to our newsletter to keep up to date on our marketing, website, design services, and tips.
                </div>
                <div className={styles.form}>
                    <Form.Group className={styles.inputFormgroup}>
                        <Form.Label>Enter email</Form.Label>
                        <Form.Control type="text" placeholder="Input placeholder" />
                    </Form.Group>
                    <button className={styles.button} data-animate-on-scroll>
                        <div className={styles.submit}>Submit</div>
                    </button>
                </div>
            </div>
            <div className={styles.logo}>
                <img className={styles.frameIcon} alt="" src="/frame1.svg" />
                <div className={styles.text}>SCSS</div>
            </div>
        </div>
    );
};

export default FooterForm;
