import React from 'react';

import Vectors from '../../assets/img/Vectors.png';
import DarkMode from '../../assets/img/DarkMode.png';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';

const ForgotPass = () => {
    const {
        register,
        handleSubmit,
        formState: { errors },
    } = useForm();

    const onSubmit = (data) => {
        console.log(data);
    };
    return (
        <div className="h-screen w-screen">
            <div className="bg-slate-100 h-full w-full relative flex flex-col ">
                <div className="mx-auto my-auto gap-y-4 flex flex-col lg:w-[727px] lg:h-[581px] md:w-[527px] md:h-[581px] w-[327px] ">
                    <div className="flex flex-col gap-y-2 font-medium">
                        <h1 className="text-4xl font-sans items-center justify-center flex">Smart card ID services </h1>
                        <div className="items-center justify-center flex gap-x-2 text-2xl">Forgot password.</div>

                        <div className="items-center justify-center flex gap-x-2 ">
                            If you have registered by phone number, email, enter your login account here
                        </div>
                        <div className=" items-center justify-center lg:mb-10 md:mb-10 mb-4 gap-x-2 mx-auto">
                            We will send you an email with a recovery link to reset your password.
                        </div>
                    </div>
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="flex flex-col lg:gap-y-10 md:gap-y-10 gap-y-5">
                            <div className="w-full">
                                <input
                                    type="email"
                                    placeholder="Email"
                                    className="px-3 py-3 rounded-lg bg-[#052B12] text-white w-full "
                                    {...register('email', { required: true })}
                                />
                                {errors.email && <p className="text-red-600">This fied email is required</p>}
                            </div>
                        </div>
                        <div className=" flex items-center justify-center bg-[#267693] rounded px-2 py-3 mt-10 text-white hover:bg-[#052B12] cursor-pointer">
                            <button className="w-full h-full">Submit</button>
                        </div>
                    </form>
                    <div className="flex justify-end gap-x-6 underline font-medium text-blue-800 ">
                        <Link to="/signup">Sign Up</Link>
                        <Link to="/login">Sign In</Link>
                    </div>
                </div>
                <div className=" object-cover  ">
                    <img src={Vectors} alt="" className="w-full h-full " />
                </div>
                <div className="absolute bottom-6 left-6">
                    <img src={DarkMode} alt="" />
                </div>
            </div>
        </div>
    );
};

export default ForgotPass;
