import { useEffect } from "react";
import styles from "./HeroContainer.module.css";

const HeroContainer = () => {
  useEffect(() => {
    const scrollAnimElements = document.querySelectorAll(
      "[data-animate-on-scroll]"
    );
    const observer = new IntersectionObserver(
      (entries) => {
        for (const entry of entries) {
          if (entry.isIntersecting || entry.intersectionRatio > 0) {
            const targetElement = entry.target;
            targetElement.classList.add(styles.animate);
            observer.unobserve(targetElement);
          }
        }
      },
      {
        threshold: 0.15,
      }
    );

    for (let i = 0; i < scrollAnimElements.length; i++) {
      observer.observe(scrollAnimElements[i]);
    }

    return () => {
      for (let i = 0; i < scrollAnimElements.length; i++) {
        observer.unobserve(scrollAnimElements[i]);
      }
    };
  }, []);

  return (
    <div className={styles.hero}>
      <div className={styles.content}>
        <div className={styles.designBrandingContainer}>
          <p className={styles.designBranding}>{`Design & Branding`}</p>
          <p className={styles.designBranding}>Studio in New York</p>
        </div>
        <div className={styles.weCreateOutstanding}>
          We create outstanding and world-class digital products, web design,
          apps, and branding.
        </div>
        <img className={styles.icon} alt="" src="/icon.svg" />
        <button className={styles.button}>
          <div className={styles.discoverNow}>Discover Now</div>
        </button>
      </div>
      <img
        className={styles.screenShot20220717At115}
        alt=""
        src="/screen-shot-20220717-at-1154-1@2x.png"
        data-animate-on-scroll
      />
    </div>
  );
};

export default HeroContainer;
